parameters.jsCode:
// Process AI evaluation with proper data item linking
const items = $input.all();
const results = [];

for (const [index, item] of items.entries()) {
  try {
    // Get the original application data and AI evaluation
    const originalData = item.json.fullApplication || item.json;
    const aiEvaluation = item.json.output || item.json;

    // Calculate weighted score
    const weightedScore = Math.round(
      (aiEvaluation.scores.technical * 0.35) +
      (aiEvaluation.scores.aiTools * 0.20) +
      (aiEvaluation.scores.availability * 0.15) +
      (aiEvaluation.scores.culture * 0.15) +
      (aiEvaluation.scores.portfolio * 0.10) +
      (aiEvaluation.scores.communication * 0.05)
    );

    // Determine final routing based on weighted score
    let finalRouting = aiEvaluation.routing;
    if (weightedScore >= 85) finalRouting = "FAST_TRACK";
    else if (weightedScore >= 70) finalRouting = "INTERVIEW";
    else if (weightedScore >= 55) finalRouting = "TECHNICAL_TEST";
    else if (weightedScore >= 40) finalRouting = "REVIEW";
    else finalRouting = "REJECT";

    // Create urgency level based on routing
    const urgencyMap = {
      "FAST_TRACK": "üö® URGENT",
      "INTERVIEW": "‚ö° HIGH", 
      "TECHNICAL_TEST": "üìã MEDIUM",
      "REVIEW": "üîç LOW",
      "REJECT": "‚ùå NONE"
    };

    // Prepare enhanced result with proper linking
    const processedResult = {
      // Original application data
      ...originalData,

      // Processing metadata
      applicationId: item.json.applicationId || `${originalData.email.split('@')[0]}-${Date.now()}`,
      processedAt: item.json.processedAt || new Date().toISOString(),
      referralSource: item.json.referralSource || 'direct',

      // AI evaluation results
      evaluation: {
        ...aiEvaluation,
        finalScore: weightedScore,
        finalRouting: finalRouting,
        urgencyLevel: urgencyMap[finalRouting],
        evaluatedAt: new Date().toISOString(),
        processingTime: Date.now() - new Date(item.json.processedAt || new Date()).getTime()
      },

      // Routing metadata for downstream nodes
      routingData: {
        route: finalRouting,
        urgency: urgencyMap[finalRouting],
        requiresImmedateAction: finalRouting === "FAST_TRACK",
        timelineHours: {
          "FAST_TRACK": 2,
          "INTERVIEW": 48,
          "TECHNICAL_TEST": 72,
          "REVIEW": 168,
          "REJECT": 24
        }[finalRouting]
      }
    };

    results.push({
      json: processedResult,
      pairedItem: { item: index } // Proper item linking
    });

  } catch (error) {
    // Error handling with proper linking
    results.push({
      json: {
        ...item.json,
        evaluation: {
          error: error.message,
          finalRouting: "REVIEW",
          finalScore: 0,
          urgencyLevel: "üîç ERROR - MANUAL REVIEW",
          evaluatedAt: new Date().toISOString()
        }
      },
      pairedItem: { item: index }
    });
  }
}

return results;

