// Process AI evaluation with proper data item linking
const items = $input.all();
const results = [];

for (const [index, item] of items.entries()) {
  try {
    // Get the original application data
    const originalData = item.json.fullApplication || {};
    const aiResponse = item.json.message?.content || item.json.content || '{}';

    // Parse AI evaluation
    let aiEvaluation;
    try {
      // Clean the response and extract JSON
      const jsonMatch = aiResponse.match(/\{[\s\S]*\}/);
      if (jsonMatch) {
        aiEvaluation = JSON.parse(jsonMatch[0]);
      } else {
        throw new Error('No JSON found in AI response');
      }
    } catch (parseError) {
      // Fallback evaluation for parsing errors
      aiEvaluation = {
        overallScore: 50,
        routing: "REVIEW",
        scores: {
          technical: 50,
          aiTools: 50,
          availability: 50,
          culture: 50,
          portfolio: 50,
          communication: 50
        },
        analysis: {
          strengths: ["Application received"],
          concerns: ["AI evaluation parsing failed"],
          recommendations: ["Manual review required"],
          interviewFocus: ["General discussion"]
        },
        summary: "Manual review required due to AI parsing error.",
        confidence: 0.5
      };
    }

    // Calculate weighted score
    const weightedScore = Math.round(
      (aiEvaluation.scores?.technical * 0.35 || 25) +
      (aiEvaluation.scores?.aiTools * 0.20 || 10) +
      (aiEvaluation.scores?.availability * 0.15 || 7.5) +
      (aiEvaluation.scores?.culture * 0.15 || 7.5) +
      (aiEvaluation.scores?.portfolio * 0.10 || 5) +
      (aiEvaluation.scores?.communication * 0.05 || 2.5)
    );

    // Determine final routing based on weighted score
    let finalRouting = aiEvaluation.routing || "REVIEW";
    if (weightedScore >= 85) finalRouting = "FAST_TRACK";
    else if (weightedScore >= 70) finalRouting = "INTERVIEW";
    else if (weightedScore >= 55) finalRouting = "TECHNICAL_TEST";
    else if (weightedScore >= 40) finalRouting = "REVIEW";
    else finalRouting = "REJECT";

    // Create urgency level based on routing
    const urgencyMap = {
      "FAST_TRACK": "ğŸš¨ URGENT",
      "INTERVIEW": "âš¡ HIGH",
      "TECHNICAL_TEST": "ğŸ“‹ MEDIUM",
      "REVIEW": "ğŸ” LOW",
      "REJECT": "âŒ NONE"
    };

    // Prepare enhanced result with proper linking
    const processedResult = {
      // Original application data (flattened for easier access)
      name: originalData.name || 'Unknown',
      email: originalData.email || `unknown-${Date.now()}-${Math.random().toString(36).substr(2, 9)}@example.com`,
      phone: originalData.phone || '',
      location: originalData.location || '',
      linkedin: originalData.linkedin || '',
      github: originalData.github || '',
      reactExperience: originalData.reactExperience || '',
      aiToolsExperience: originalData.aiToolsExperience || '',
      portfolio: originalData.portfolio || '',
      startDate: originalData.startDate || '',
      workHours: originalData.workHours || '',
      remoteExperience: originalData.remoteExperience || '',
      whyAllign: originalData.whyAllign || '',
      startupExperience: originalData.startupExperience || '',
      technicalChallenge: originalData.technicalChallenge || '',
      salaryExpectations: originalData.salaryExpectations || '',
      questionsForUs: originalData.questionsForUs || '',
      additionalInfo: originalData.additionalInfo || '',
      submittedAt: originalData.submittedAt || new Date().toISOString(),
      referralSource: originalData.referralSource || 'direct',
      userAgent: originalData.userAgent || '',
      consent: originalData.consent || false,

      // Processing metadata
      applicationId: item.json.applicationId || `${originalData.email?.split('@')[0] || 'unknown'}-${Date.now()}`,
      processedAt: item.json.processedAt || new Date().toISOString(),

      // AI evaluation results
      evaluation: {
        ...aiEvaluation,
        finalScore: weightedScore,
        finalRouting: finalRouting,
        urgencyLevel: urgencyMap[finalRouting],
        evaluatedAt: new Date().toISOString(),
        processingTime: Date.now() - new Date(item.json.processedAt || new Date()).getTime(),
        rawAiResponse: aiResponse
      },

      // Routing metadata for downstream nodes
      routingData: {
        route: finalRouting,
        urgency: urgencyMap[finalRouting],
        requiresImmediateAction: finalRouting === "FAST_TRACK",
        timelineHours: {
          "FAST_TRACK": 2,
          "INTERVIEW": 48,
          "TECHNICAL_TEST": 72,
          "REVIEW": 168,
          "REJECT": 24
        }[finalRouting]
      }
    };

    results.push({
      json: processedResult,
      pairedItem: { item: index }
    });

  } catch (error) {
    // Error handling with proper linking
    const originalData = item.json.fullApplication || {};
    results.push({
      json: {
        // Basic data even in error case
        name: originalData.name || 'Error Processing',
        email: originalData.email || `error-${Date.now()}-${Math.random().toString(36).substr(2, 9)}@example.com`,
        phone: originalData.phone || '',
        location: originalData.location || '',
        linkedin: originalData.linkedin || '',
        github: originalData.github || '',
        reactExperience: originalData.reactExperience || '',
        aiToolsExperience: originalData.aiToolsExperience || '',
        portfolio: originalData.portfolio || '',
        startDate: originalData.startDate || '',
        workHours: originalData.workHours || '',
        remoteExperience: originalData.remoteExperience || '',
        whyAllign: originalData.whyAllign || '',
        startupExperience: originalData.startupExperience || '',
        technicalChallenge: originalData.technicalChallenge || '',
        salaryExpectations: originalData.salaryExpectations || '',
        questionsForUs: originalData.questionsForUs || '',
        additionalInfo: originalData.additionalInfo || '',
        submittedAt: originalData.submittedAt || new Date().toISOString(),
        referralSource: originalData.referralSource || 'direct',
        userAgent: originalData.userAgent || '',
        consent: originalData.consent || false,
        applicationId: item.json.applicationId || `error-${Date.now()}`,
        processedAt: item.json.processedAt || new Date().toISOString(),
        evaluation: {
          error: error.message,
          finalRouting: "REVIEW",
          finalScore: 0,
          urgencyLevel: "ğŸ” ERROR - MANUAL REVIEW",
          evaluatedAt: new Date().toISOString()
        }
      },
      pairedItem: { item: index }
    });
  }
}

return results;

