// Simplified processing with better error handling
const items = $input.all();
const results = [];

for (const [index, item] of items.entries()) {
  try {
    // Get the original application data
    const originalData = item.json.fullApplication || {};

    // Simple scoring based on content length and keywords
    let score = 30; // base score

    // Score based on content quality
    if (originalData.reactExperience && originalData.reactExperience.length > 100) score += 15;
    if (originalData.aiToolsExperience && originalData.aiToolsExperience.length > 50) score += 10;
    if (originalData.portfolio && originalData.portfolio.length > 50) score += 10;
    if (originalData.whyAllign && originalData.whyAllign.length > 50) score += 10;
    if (originalData.startupExperience && originalData.startupExperience.length > 50) score += 10;
    if (originalData.technicalChallenge && originalData.technicalChallenge.length > 50) score += 10;

    // Availability bonus
    if (originalData.startDate === 'immediately') score += 5;

    // Determine routing
    let routing = 'REVIEW';
    if (score >= 85) routing = 'FAST_TRACK';
    else if (score >= 70) routing = 'INTERVIEW';
    else if (score >= 55) routing = 'TECHNICAL_TEST';
    else if (score < 40) routing = 'REJECT';

    const urgencyMap = {
      'FAST_TRACK': 'ðŸš¨ URGENT',
      'INTERVIEW': 'âš¡ HIGH',
      'TECHNICAL_TEST': 'ðŸ“‹ MEDIUM',
      'REVIEW': 'ðŸ” LOW',
      'REJECT': 'âŒ NONE'
    };

    // Prepare result with all data intact
    const processedResult = {
      // Copy all original data
      ...originalData,

      // Add processing metadata
      applicationId: item.json.applicationId || `${originalData.email?.split('@')[0] || 'unknown'}-${Date.now()}`,
      processedAt: item.json.processedAt || new Date().toISOString(),

      // Add evaluation results
      evaluation: {
        finalScore: score,
        finalRouting: routing,
        urgencyLevel: urgencyMap[routing],
        evaluatedAt: new Date().toISOString(),
        processingTime: 100,
        summary: `Automated scoring: ${score}/100 points`,
        method: 'simplified'
      }
    };

    results.push({
      json: processedResult,
      pairedItem: { item: index }
    });

  } catch (error) {
    console.error('Processing error:', error);
    // Fallback with original data
    const originalData = item.json.fullApplication || {};
    results.push({
      json: {
        ...originalData,
        applicationId: `error-${Date.now()}`,
        processedAt: new Date().toISOString(),
        evaluation: {
          finalScore: 50,
          finalRouting: 'REVIEW',
          urgencyLevel: 'ðŸ” ERROR - MANUAL REVIEW',
          evaluatedAt: new Date().toISOString(),
          error: error.message
        }
      },
      pairedItem: { item: index }
    });
  }
}

return results;

